import  React from "react";
import axios from "axios";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { useContext, useState, useEffect } from "react";
import { AuthContext } from "../context/AuthProvider";
import { HttpHeadersContext } from "../context/HttpHeadersProvider";

function BbsAnswer() {

	const { auth, setAuth } = useContext(AuthContext)
	const { headers, setHeaders } = useContext(HttpHeadersContext);

	const navigate = useNavigate();

	const { parentSeq } = useParams(); // Î∂ÄÎ™® Í∏Ä Î≤àÌò∏

	const location = useLocation();
	const { parentBbs } = location.state;

	const [title, setTitle] = useState("");
	const [content, setContent] = useState("");

	const changeTitle = (event) => {
		setTitle(event.target.value);
	}

	const changeContent = (event) => {
		setContent(event.target.value);
	}


	const createBbsAnswer = async () => {

		const req = {
			id: localStorage.getItem("id"),
			title: title,
			content: content
		}

		await axios.post(`http://localhost:3000/bbs/${parentSeq}/answer`, req, {headers: headers})
		.then((resp) => {
			console.log("[BbsAnswer.js] createBbsAnswer() success :D");
			console.log(resp.data);

			alert("ÎãµÍ∏ÄÏùÑ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Îì±Î°ùÌñàÏäµÎãàÎã§ :D");
			navigate(`/bbsdetail/${resp.data.seq}`); // ÏÉàÎ°≠Í≤å Îì±Î°ùÌïú ÎãµÍ∏Ä ÏÉÅÏÑ∏Î°ú Ïù¥Îèô
		})
		.catch((err) => {
			console.log("[BbsAnswer.js] createBbsAnswer() error :<");
			console.log(err);


		});

	}

	useEffect(() => {
		if (!auth) {
			alert("Î°úÍ∑∏Ïù∏ Ìïú ÏÇ¨Ïö©ÏûêÎßå Í≤åÏãúÍ∏ÄÏóê ÎåÄÌïú ÎãµÍ∏ÄÏùÑ ÏûëÏÑ±Ìï† Ïàò ÏûàÏäµÎãàÎã§ !");
			navigate(-1);
		}
	}, []);

	return (
		<div>
			{/* Î∂ÄÎ™® Í≤åÏãúÍ∏Ä Ï†ïÎ≥¥ */}
			<table className="table">
				<tbody>
					<tr>
						<th className="table-primary">ÏûëÏÑ±Ïûê</th>
						<td>
							<input type="text" className="form-control" value={parentBbs.id} size="50px" readOnly />
						</td>
					</tr>

					<tr>
						<th className="table-primary">Ï†úÎ™©</th>
						<td>
							<input type="text" className="form-control" value={parentBbs.title} size="50px" readOnly />
						</td>
					</tr>
				</tbody>
			</table><br/><br/>

			{/* ÎãµÍ∏Ä ÏûëÏÑ± */}
			<h3>üìå Reply</h3>
			<table className="table">
				<tbody>
					<tr>
						<th className="table-primary">ÏûëÏÑ±Ïûê</th>
						<td>
							<input type="text" className="form-control"  value={localStorage.getItem("id")} size="50px" readOnly />
						</td>
					</tr>

					<tr>
						<th className="table-primary">Ï†úÎ™©</th>
						<td>
							<input type="text" className="form-control" value={title} onChange={changeTitle} size="50px" />
						</td>
					</tr>

					<tr>
						<th className="table-primary">ÎÇ¥Ïö©</th>
						<td>
							<textarea className="form-control" value={content} onChange={changeContent} rows="10"></textarea>
						</td>
					</tr>
				</tbody>
			</table>

			<div className="my-5 d-flex justify-content-center">
				<button className="btn btn-outline-secondary" onClick={createBbsAnswer}><i className="fas fa-pen"></i> ÎãµÍ∏ÄÎã¨Í∏∞</button>
			</div>
		</div>
	);
}

export default BbsAnswer;
